mapper.insert:
insert into yt_channel (channel_id, keyword, image, title, subscription_count, view_count, description, video_count) values
#{channel.channelId}, #{channel.keyword}, #{channel.image}, #{channel.title}, #{channel.subscriptionCount}, #{channel.viewCount}, #{channel.description}, #{channel.videoCount}
on duplicate key update channel_id = values(channel_id), keyword = values(keyword), image = values(image), title = values(title), subscription_count = values(subscription_count), view_count = values(view_count), description = values(description), video_count = values(video_count)
