여기서부터 channel-kingtag 쿼리
SELECT A.id, A.channel_id, A.keyword, A.image, A.title, A.subscription_count, A.view_count, A.description FROM yt_channel A INNER JOIN yt_channel_tag B ON A.id = B.channel_id INNER JOIN yt_tag C ON C.id = B.tag_id WHERE C.id = #{id} ORDER BY A.subscription_count DESC
select C.id, C.tag_name, A.parent_tag_id from yt_kingtag A INNER JOIN yt_channel_tag B ON A.tag_ptr_id = B.tag_id INNER JOIN yt_tag C ON B.tag_id = C.id WHERE B.channel_id = #{channelId}
insert into yt_channel_tag (channel_id, tag_id) values
#{channelId}, #{tagId}
delete from yt_channel_tag where channel_id = #{channelId} and tag_id in
#{tagId}
