mapper.insert:
INSERT INTO
		yt_kingtag (tag_ptr_id, parent_tag_id)
VALUES(
		#{id},
		#{parentTagId}
)
ON DUPLICATE KEY UPDATE
		tag_ptr_id = #{id}, parent_tag_id = #{parentTagId}

mapper.update:
update
		yt_kingtag
set
		parent_tag_id = #{parentTagId}
where
		tag_ptr_id = #{id}

update.foreach:
update
	yt_kingtag
set
	parent_tag_id = #{kingtag.parentTagId}
where
	tag_ptr_id = #{kingtag.id}
